# AI Power Pack v2.4 - PowerShell Installer# ==========================================Write-Host ""Write-Host "========================================" -ForegroundColor CyanWrite-Host "   AI Power Pack v2.4" -ForegroundColor YellowWrite-Host "   PowerShell Installer" -ForegroundColor YellowWrite-Host "========================================" -ForegroundColor CyanWrite-Host ""# Check PythonWrite-Host "[CHECK] Checking Python..." -ForegroundColor Cyantry {    $pythonVersion = python --version 2>&1    Write-Host "[SUCCESS] Python installed: $pythonVersion" -ForegroundColor Green} catch {    Write-Host "[ERROR] Python not installed" -ForegroundColor Red    Write-Host "Please install Python 3.8+ from https://www.python.org/" -ForegroundColor Yellow    Read-Host "Press Enter to exit"    exit 1}Write-Host ""# Get script directory$scriptDir = Split-Path -Parent $MyInvocation.MyCommand.Path$configDir = Join-Path $scriptDir "config"# Configure Claude DesktopWrite-Host "[STEP 1/4] Configuring Claude Desktop..." -ForegroundColor Cyan$claudePath = Join-Path $env:APPDATA "Claude"if (Test-Path $claudePath) {    $claudeMd = Join-Path $configDir "CLAUDE.md"    if (Test-Path $claudeMd) {        try {            $configFile = Join-Path $claudePath "claude_desktop_config.json"            $content = Get-Content -Path $claudeMd -Encoding UTF8 -Raw                        $config = @{                customInstructions = @{                    global = $content                    version = "2.4"                    auto_deployed = $true                }            }                        $config | ConvertTo-Json -Depth 10 | Set-Content -Path $configFile -Encoding UTF8            Write-Host "  [SUCCESS] Claude Desktop configured" -ForegroundColor Green        } catch {            Write-Host "  [ERROR] Failed to configure Claude: $_" -ForegroundColor Red        }    }} else {    Write-Host "  [SKIP] Claude Desktop not found" -ForegroundColor Yellow}Write-Host ""# Configure VSCodeWrite-Host "[STEP 2/4] Configuring VSCode..." -ForegroundColor Cyan$vscodePath = Join-Path $env:APPDATA "Code\User"if (Test-Path $vscodePath) {    $copilotMd = Join-Path $configDir "copilot-instructions.md"    if (Test-Path $copilotMd) {        try {            $destPath = Join-Path $vscodePath "copilot-instructions.md"            Copy-Item -Path $copilotMd -Destination $destPath -Force                        $settingsFile = Join-Path $vscodePath "settings.json"            $settings = @{                "github.copilot.chat.codeGeneration.instructions" = @(                    @{                        file = $destPath                        text = "Follow AI Power Pack v2.4 standards"                    }                )                "github.copilot.enable" = @{                    "*" = $true                    plaintext = $true                    markdown = $true                }            }                        $settings | ConvertTo-Json -Depth 10 | Set-Content -Path $settingsFile -Encoding UTF8            Write-Host "  [SUCCESS] VSCode configured" -ForegroundColor Green        } catch {            Write-Host "  [ERROR] Failed to configure VSCode: $_" -ForegroundColor Red        }    }} else {    Write-Host "  [SKIP] VSCode not found" -ForegroundColor Yellow}Write-Host ""# Install dependenciesWrite-Host "[STEP 3/4] Installing Python dependencies..." -ForegroundColor Cyan$packages = "requests", "cryptography", "pyside6", "fastapi", "sqlmodel", "uvicorn"foreach ($pkg in $packages) {    Write-Host "  Installing $pkg..." -NoNewline    pip install $pkg -q 2>$null    if ($LASTEXITCODE -eq 0) {        Write-Host " OK" -ForegroundColor Green    } else {        Write-Host " SKIP" -ForegroundColor Yellow    }}Write-Host ""# CompleteWrite-Host "[STEP 4/4] Installation complete!" -ForegroundColor CyanWrite-Host ""Write-Host "========================================" -ForegroundColor CyanWrite-Host "   Installation Complete!" -ForegroundColor GreenWrite-Host "========================================" -ForegroundColor CyanWrite-Host ""Write-Host "Next Steps:" -ForegroundColor YellowWrite-Host "  1. Restart Claude Desktop"Write-Host "  2. Restart VSCode"Write-Host "  3. Start coding with AI Power Pack!"Write-Host ""Write-Host "Thank you for using AI Power Pack v2.4!" -ForegroundColor CyanWrite-Host ""Read-Host "Press Enter to exit"